parser = argparse.ArgumentParser()

parser = argparse.ArgumentParser()
parser.add_argument('-i', '--indir', action='store', help='image path', default='PreSegmentation')
parser.add_argument('-o', '--outdir', action='store', help='output dir', default='Segmentation')

args = parser.parse_args()



flist = []
for (dirpath, dirnames, filenames) in os.walk(args.indir):
    flist.extend(filenames)
    break

print("Images:", flist)


if not os.path.exists(args.outdir):
    os.makedirs(args.outdir)


def resize(img, basewidth=500):
    wpercent = (basewidth/float(img.size[0]))
    hsize = int((float(img.size[1])*float(wpercent)))
    return img.resize((basewidth,hsize))

def palette_color(i, pal=PAL):
    idx = 3 * i
    return (pal[idx], pal[idx+1], pal[idx +2])

   
for fname in flist:
    fpath = args.indir + '/' + fname  
    im = Image.open(fpath )
    print(im.format, im.mode, im.size)

    if im.mode != 'RGB':
        print("Converting")
        im = im.convert('RGB')

    img = resize(im)
        
    imc = img.quantize(colors=3, method=1)
    c_list = imc.getcolors()
    c_pal  = imc.getpalette()
    B_PAL = -1
    W_PAL = -1
    for c_item in c_list:
        color = c_item[1]
        p_c = palette_color(color, c_pal)
        if (p_c[0] < 32 and p_c[1] < 32 and p_c[2] < 32):
            B_PAL = c_item[1]
        elif (p_c[0] > 224 and p_c[1] > 224 and p_c[2] > 224):
            W_PAL = c_item[1]

    if (B_PAL == -1 and W_PAL == -1):
        raise Exception("Could not find black or white pallete")

    imc.putpalette(PAL)
    row, col = imc.size

    # Switch pallettes
    for y in range(col):
        for x in range(row):
            if imc.getpixel((x,y)) == W_PAL:
                imc.putpixel((x,y), 0)
            elif imc.getpixel((x,y)) == B_PAL:
                imc.putpixel((x,y), W_PAL)


    imrgb = imc.convert('RGB')
    imgray = cv2.cvtColor(np.array(imrgb),cv2.COLOR_RGB2GRAY)
    edged = cv2.Canny(imgray, 100, 200)
    _, cnts, _ = cv2.findContours(edged.copy(), cv2.RETR_TREE, cv2.CHAIN_APPROX_SIMPLE)
    cvimgd = cv2.drawContours(np.array(imrgb), cnts, -1, (255,255,255), 2)
    for y in range(col):
        for x in range(row):
            if((cvimgd[y,x,0] == 255) and (cvimgd[y,x,1] == 255) and (cvimgd[y,x,2] == 255)):
                imc.putpixel((x,y), 255)

    imc.save(args.outdir + '/' + fname)
